import{_ as o,c as a,a2 as r,o as t}from"./chunks/framework.DChxh5P2.js";const c=JSON.parse('{"title":"中文咖啡厅 海报长期展览协议","description":"","frontmatter":{},"headers":[],"relativePath":"CnCoffeeBar/collaboration.md","filePath":"CnCoffeeBar/collaboration.md","lastUpdated":null}'),i={name:"CnCoffeeBar/collaboration.md"};function n(_,l,e,s,h,g){return t(),a("div",null,l[0]||(l[0]=[r('<h1 id="中文咖啡厅-海报长期展览协议" tabindex="-1">中文咖啡厅 海报长期展览协议 <a class="header-anchor" href="#中文咖啡厅-海报长期展览协议" aria-label="Permalink to &quot;中文咖啡厅 海报长期展览协议&quot;">​</a></h1><hr><p><strong>甲方（展示方）</strong>：<br> 主要负责人：Hrenact<br> QQ ：2278393974<br> 微信：hrenact<br> 邮箱：WhiteYoke@163.com</p><p><strong>乙方（海报提供方）</strong>：<br> 主要负责人：__________<br> QQ ：__________<br> 微信：__________<br> 邮箱：__________</p><p>为了推动双方的合作，甲乙双方本着平等自愿、互利共赢的原则，就乙方海报的展览事宜达成以下协议：</p><hr><h2 id="第一条-合作目的" tabindex="-1"><strong>第一条 合作目的</strong> <a class="header-anchor" href="#第一条-合作目的" aria-label="Permalink to &quot;**第一条 合作目的**&quot;">​</a></h2><ol><li>甲方为乙方提供展览空间，用于展示乙方提供的海报，以促进乙方社区或产品等内容的宣传推广。</li></ol><h2 id="第二条-合作内容" tabindex="-1"><strong>第二条 合作内容</strong> <a class="header-anchor" href="#第二条-合作内容" aria-label="Permalink to &quot;**第二条 合作内容**&quot;">​</a></h2><ol><li><strong>展示内容</strong>：乙方需提供符合约定的海报（设计稿、成品或电子文件）。内容包括但不限于品牌标识、产品信息、活动宣传。</li><li><strong>展示位置</strong>：甲方将海报展示在 中文咖啡厅 室内建筑入口处 左或右侧。</li><li><strong>展示形式</strong>：__________。</li></ol><h2 id="第三条-合作时间" tabindex="-1"><strong>第三条 合作时间</strong> <a class="header-anchor" href="#第三条-合作时间" aria-label="Permalink to &quot;**第三条 合作时间**&quot;">​</a></h2><ol><li>展览起始日期：__________</li><li>展览结束日期：__________</li></ol><h2 id="第四条-双方权利与义务" tabindex="-1"><strong>第四条 双方权利与义务</strong> <a class="header-anchor" href="#第四条-双方权利与义务" aria-label="Permalink to &quot;**第四条 双方权利与义务**&quot;">​</a></h2><ol><li><strong>甲方权利与义务</strong>： <ul><li>按约定时间展示乙方海报。</li><li>有权拒绝/停止展示不符合约定的内容（如违反法律法规或海报宣传内容有误）。</li><li>对乙方中的商业秘密负有保密义务，未经对方同意，不得向第三方透露。</li><li>甲方有权在不侵犯乙方利益的前提下展览前提下展览其他乙方海报。</li></ul></li><li><strong>乙方权利与义务</strong>： <ul><li>提供设计完整、符合约定的海报，并提供产品样本的邮寄（如有）。</li><li>确保海报内容真实、合法，无侵犯第三方权益的行为。</li><li>若需撤回或更换海报，应提前一个工作日通知甲方。</li></ul></li></ol><h2 id="第五条-费用与支付" tabindex="-1"><strong>第五条 费用与支付</strong> <a class="header-anchor" href="#第五条-费用与支付" aria-label="Permalink to &quot;**第五条 费用与支付**&quot;">​</a></h2><ol><li><strong>费用</strong>：乙方需支付甲方展览费__________元（人民币）。</li><li><strong>支付方式</strong>：支付宝/微信转账。</li><li><strong>支付时间</strong>：__________。（注：乙方应在展览前三个工作日内支付展览费用）</li></ol><p>时间越长优惠越高 / 老客户优惠 / 老带新同时优惠</p><h2 id="第六条-审核流程" tabindex="-1"><strong>第六条 审核流程</strong> <a class="header-anchor" href="#第六条-审核流程" aria-label="Permalink to &quot;**第六条 审核流程**&quot;">​</a></h2><ol><li>乙方添加甲方的联系方式后，应说明来意并提供初步计划。</li><li>甲方同意后，乙方应按照以下分类提供海报的具体内容： <ul><li><strong>社区宣传</strong>：社区的主要活动，目标人群以及活动地点等。</li><li><strong>产品宣传</strong>：产品的主要功能，关键卖点以及官网链接等。</li></ul></li><li>甲方根据实际情况修改乙方提供的海报，确保符合展览标准（可选）。</li><li>双方确认展览内容无误后，乙方须按照此协议《第五条 费用与支付》支付相关费用。</li><li>甲方按照约定的展览起始日期更新海报至 中文咖啡厅 地图内。</li></ol><h2 id="第七条-违约责任" tabindex="-1"><strong>第七条 违约责任</strong> <a class="header-anchor" href="#第七条-违约责任" aria-label="Permalink to &quot;**第七条 违约责任**&quot;">​</a></h2><ol><li>甲方未按约定展示海报，应在三个工作日内退还相应费用并承担额外 10% 违约金。</li><li>因乙方社区或产品导致不良舆论时，甲方有权在通知乙方的情况下取消展示海报，且已支付费用概不退还。</li></ol><h2 id="第八条-合作终止" tabindex="-1"><strong>第八条 合作终止</strong> <a class="header-anchor" href="#第八条-合作终止" aria-label="Permalink to &quot;**第八条 合作终止**&quot;">​</a></h2><ol><li>到达展览结束日期后，自然终止协议。</li><li>双方协商同意后可提前终止协议。</li><li>如任何一方严重违反协议，另一方有权单方面终止合作。</li></ol><h2 id="第九条-其它事项" tabindex="-1"><strong>第九条 其它事项</strong> <a class="header-anchor" href="#第九条-其它事项" aria-label="Permalink to &quot;**第九条 其它事项**&quot;">​</a></h2><ol><li>此协议于乙方签名日期开始生效。</li><li>此协议甲乙双方各持一份电子形式。</li></ol><hr><p><strong>协议生效形式及日期</strong>：</p><p><strong>甲方签名</strong>：</p><p><strong>乙方签名</strong>：</p>',29)]))}const b=o(i,[["render",n]]);export{c as __pageData,b as default};
