import{_ as d}from"./chunks/OpenEditorScript.BOljgYz_.js";import{_ as e,c as o,a2 as r,o as a}from"./chunks/framework.DChxh5P2.js";const c="/HrenactNET/images/E-Book/Prefab.png",i="/HrenactNET/images/E-Book/PrefabScript.png",s="/HrenactNET/images/E-Book/EditorScript.png",n="/HrenactNET/images/E-Book/Console.png",P=JSON.parse('{"title":"预制件层级","description":"","frontmatter":{"prev":{"text":"使用方法","link":"E-Book/HowToUse"},"next":{"text":"示例页面","link":"E-Book/SamplePage"}},"headers":[],"relativePath":"E-Book/Information.md","filePath":"E-Book/Information.md","lastUpdated":null}'),l={name:"E-Book/Information.md"};function p(h,t,m,k,x,u){return a(),o("div",null,t[0]||(t[0]=[r('<h1 id="预制件层级" tabindex="-1">预制件层级 <a class="header-anchor" href="#预制件层级" aria-label="Permalink to &quot;预制件层级&quot;">​</a></h1><p>预制件本身进行了一定的分类管理，使其观感更加舒适，也便于制作变体。</p><p><img src="'+c+'" alt="Prefab"></p><table tabindex="0"><thead><tr><th>编号</th><th>名称</th><th>用途</th></tr></thead><tbody><tr><td>1</td><td>Background/EditorOnly_Background</td><td>制作页面时的辅助背景，在上传或运行时会自动删除。</td></tr><tr><td>2</td><td>Book_Title</td><td>电子书的书名。</td></tr><tr><td>3</td><td>Put_Pages_Inside_ME</td><td>存放书本的所有页面。</td></tr></tbody></table><h1 id="预制件脚本" tabindex="-1">预制件脚本 <a class="header-anchor" href="#预制件脚本" aria-label="Permalink to &quot;预制件脚本&quot;">​</a></h1><p>预制件默认附带已经设定好的脚本，你可以开箱即用。如果你想高度自定义，下方也列出了每个部件的用途，供参考。</p><p><img src="'+i+'" alt="PrefabScript"></p><table tabindex="0"><thead><tr><th>编号</th><th>名称</th><th>用途</th></tr></thead><tbody><tr><td>1</td><td>Book_Page_Key</td><td>持久化时使用的键值，请确保每本电子书都使用不同的值，并且与其它支持持久化数据的脚本的值没有冲突。</td></tr><tr><td>2</td><td>Book Pages</td><td>书本页面列表，列表中的顺序应与书本层级 Inspector 中<code>Put_Pages_Inside_ME</code>内的实际页面顺序一致。</td></tr><tr><td>3</td><td>Previous Page</td><td>按下时显示上一页的按钮（string<code>OnClickPreviousPage</code>）。</td></tr><tr><td>4</td><td>Next Page</td><td>按下时显示下一页的按钮（string<code>OnClickNextPage</code>）。</td></tr><tr><td>5</td><td>First Page</td><td>按下时回到第一页的按钮（string<code>OnClickFirstPage</code>）。</td></tr><tr><td>6</td><td>Page Slider</td><td>拖动来切换页面的滑动条。</td></tr><tr><td>7</td><td>Page Number</td><td>显示当前页面以及总页数的文本。</td></tr><tr><td>8</td><td>Lock Button</td><td>打开或关闭 Pickup 的按钮（string<code>_LockButtonToggle</code>）。</td></tr><tr><td>9</td><td>Pickup State</td><td>显示 Pickup 状态的图像。</td></tr><tr><td>10</td><td>Pickup Enabled Sprite</td><td>当 Pickup 开启时显示的精灵图。</td></tr><tr><td>11</td><td>Pickup Disabled Sprite</td><td>当 Pickup 关闭时显示的精灵图。</td></tr></tbody></table><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p><code>Book_Sample_Persistence.prefab</code>使用的脚本为<code>BookPersistence.cs</code>。与<code>Book_Sample.prefab</code>使用的<code>Book.cs</code>不同的是，此脚本支持数据持久化，它将自动保存你翻阅到的页面并在下次进入世界时加载。</p></div><h1 id="text-set-tool" tabindex="-1">Text Set Tool <a class="header-anchor" href="#text-set-tool" aria-label="Permalink to &quot;Text Set Tool&quot;">​</a></h1><p>此 Unity 编辑器拓展脚本一定程度上简化了大量文本需要添加时的复杂程度，允许你自动化填充文本。</p><p>你可以在 Unity 窗口的顶栏<code>Tools/HrenactNET/Text Set Tool</code>打开它。</p><p><img src="'+d+'" alt="OpenEditorScript"></p><h2 id="界面介绍" tabindex="-1">界面介绍 <a class="header-anchor" href="#界面介绍" aria-label="Permalink to &quot;界面介绍&quot;">​</a></h2><p><img src="'+s+'" alt="EditorScript"></p><table tabindex="0"><thead><tr><th>编号</th><th>名称</th><th>用途</th></tr></thead><tbody><tr><td>1</td><td>Full Text:</td><td>将被自动填充的文本。如果使用导入 *.txt 文件，此处将显示文件内的文本。</td></tr><tr><td>2</td><td>Load Text from *.txt File (UTF-8)</td><td>选择你需要自动生成的 *.txt 文本文件。</td></tr><tr><td>3</td><td>Is Set Texts Color</td><td>如果勾选此复选框，则自动设定<code>Text Components</code>列表中<code>Text</code>组件的颜色。</td></tr><tr><td>4</td><td>Text Color</td><td>自动设定<code>Text</code>组件的目标颜色。</td></tr><tr><td>5</td><td>Text Components</td><td>将被自动填充的<code>Text</code>组件列表，列表中的顺序应对应书本实际的<code>Text</code>组件顺序。</td></tr><tr><td>6</td><td>Start Fill Text</td><td>按下后将把<code>Full Text</code>中的文本按<code>Text Components</code>中的顺序依次填入<code>Text</code>组件中。</td></tr></tbody></table><p>如果在你按下<code>Start Fill Text</code>后没有反应，请检查 Unity 控制台是否拥有提示信息输出，如下图所示：</p><p><img src="'+n+'" alt="Console"></p><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p>当<code>Full Text:</code>于活跃状态（输入框边框为蓝色）时，<code>Load Text from *.txt File (UTF-8)</code>将会失效。你可以通过点击其它窗口来解除活跃状态。</p></div>',19)]))}const T=e(l,[["render",p]]);export{P as __pageData,T as default};
